	#region ☆ Override Methods --------------------------------------- 撰寫人員：fen

	// =========================================================================
	/// <summary>
	/// 函式描述：資料控制項容器做相關處理之後針對特殊或不能處理的子控制項做相關處理（覆蓋上層）
	/// </summary>
	/// <param name="acto_Child">資料控制項容器上的子控制項</param>
	/// <param name="as_Value">子控制項對應欄位的值(如為取出則沒去空白)</param>
	/// <param name="a_Action">處理種類–新增(New)；取出(Get)；放入(Set)；繫結(Bind)</param>
	/// <param name="adrv_Row">當筆資料列</param>
	/// <returns>成功(true)；失敗(false)</returns>
	protected override bool uf_DwF_DataAfter(Control acto_Child, ref string as_Value, DwActions a_Action, DataRowView adrv_Row)
	{
		if (a_Action == DwActions.New ) {
			if (acto_Child == dwF_hmc101_passed)
			{									dwF_hmc101_passed.Checked = this.uf_Dg_BindBool("Y", as_Value);
				return true;
			}
			if (acto_Child == dwF_hmc101_rejectepaper)
			{									dwF_hmc101_rejectepaper.Checked = this.uf_Dg_BindBool("Y", as_Value);
				return true;
			}
			if (acto_Child == dwF_hmc101_stop)
			{
				dwF_hmc101_stop.Checked = this.uf_Dg_BindBool("Y", as_Value);
				return true;
			}
			if (acto_Child == dwF_hmc101_addid_h)
			{
				dwF_hmc101_addid_b.DataTextField = "";
				Dbcfg.uf_Bind(dwF_hmc101_addid_b, "hca205", "", " AND (1 = 2)", false);
			}
		}
		else if (a_Action == DwActions.Get)
		{
			if (acto_Child == dwF_hmc101_passed)
			{									dwF_hmc101_passed.Checked = this.uf_Dg_BindBool("Y", as_Value);
				return true;
			}
			if (acto_Child == dwF_hmc101_rejectepaper)
			{									dwF_hmc101_rejectepaper.Checked = this.uf_Dg_BindBool("Y", as_Value);
				return true;
			}
			if (acto_Child == dwF_hmc101_stop)
			{
				dwF_hmc101_stop.Checked = this.uf_Dg_BindBool("Y", as_Value);
				return true;
			}
			if (acto_Child == dwF_hmc101_addid_b)
			{
				dwF_hmc101_addid_OnTextChanged(null, EventArgs.Empty);
			}

		}
		else if (a_Action == DwActions.Set)
		{
			if (acto_Child == dwF_hmc101_passed)
			{
				as_Value = this.uf_Dg_BindBool("Y,N", dwF_hmc101_passed.Checked);
				return true;
			}
			if (acto_Child == dwF_hmc101_rejectepaper)
			{
				as_Value = this.uf_Dg_BindBool("Y,N", dwF_hmc101_rejectepaper.Checked);
				return true;
			}
			if (acto_Child == dwF_hmc101_stop)
			{
				as_Value = this.uf_Dg_BindBool("Y,N", dwF_hmc101_stop.Checked);
				return true;
			}

		}
		else if (a_Action == DwActions.Bind)
		{

		}
		if (acto_Child == dwF_hmc101_id)
		{
			int li_count = 0;
			DbMethods.uf_ExecSQL("SELECT COUNT(*) FROM hmc101_102 WHERE hmc102_id in (1,2) AND hmc101_id ='" + as_Value + "'", ref li_count);
			if (li_count > 0)
			{
				u_Edit_F.ibt_Insert.Enabled = false;
				u_Edit_F.ibt_Update.Enabled = false;
				u_Edit_F.ibt_Delete.Enabled = false;
			}
			else
			{
				u_Edit_F.ibt_Insert.Enabled = true;
				u_Edit_F.ibt_Update.Enabled = true;
				u_Edit_F.ibt_Delete.Enabled = true;
			}
		}


        return true;
	}

