	// add by rong 2009/09/09
	// =========================================================================
	/// <summary>
	/// 事件描述：輸入郵遞區號時
	/// </summary>
	protected void dwF_hmc101_addid_OnTextChanged(object sender, EventArgs e)
	{
		string ls_hca212_id = "";
		if (dwF_hmc101_addid.Text.Trim().Length >= 3)
		{
			int li_count = 0;
			DbMethods.uf_ExecSQL("SELECT count(*) FROM hca205 where hca205_id = Substring('" + dwF_hmc101_addid.Text.Trim() + "',1,3)", ref li_count);
			if (li_count == 0)
			{
				uf_Msg("此郵遞區號不存在！請重新輸入");
				return;
			}
			DbMethods.uf_ExecSQL("SELECT hca205_hca212id FROM hca205 WHERE hca205_id ='" + dwF_hmc101_addid.Text.Trim().Substring(0, 3) + "'", ref ls_hca212_id);
			dwF_hmc101_addid_h.SelectedValue = ls_hca212_id;
			dwF_hmc101_addid_b.DataTextField = "";
			Dbcfg.uf_Bind(dwF_hmc101_addid_b, "hca205", "", " AND hca205_hca212id ='" + ls_hca212_id + "'", false);
			dwF_hmc101_addid_b.SelectedValue = dwF_hmc101_addid.Text.Trim().Substring(0, 3);
			dwF_hmc101_addot.Text = dwF_hmc101_addid_b.SelectedItem.ToString().Trim();
		}
	}

